import"./hamburger-a33614a7.js";const h=document.querySelector("#sortBtn"),u=document.querySelector("#sortBy"),f=document.querySelector("#sortOrder");h.addEventListener("click",()=>{i(u.value,f.value)});function i(m,p){fetch(`http://localhost:3000/api/v1/reviews?sortBy=${m}&sortOrder=${p}`).then(e=>e.json()).then(e=>{console.log(e);const c=document.getElementById("reviews-container");c.innerHTML="",e.forEach(n=>{const t=document.createElement("div");t.classList.add("review-card");const s=document.createElement("h3");s.textContent=`${n.name} ${n.firstname}`,t.appendChild(s);const r=document.createElement("p");r.textContent=n.message,t.appendChild(r);const o=document.createElement("div");o.classList.add("score");let a="";for(let l=0;l<n.score;l++)a+='<span><i class="fa-solid fa-star"></i></span>';o.innerHTML=a,t.appendChild(o);const d=document.createElement("p");d.textContent=n.time,t.appendChild(d),c.appendChild(t)})}).catch(e=>{console.error("Error fetching reviews:",e)})}i();
